      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>vs-checkbox</span> <span class="token attr-name">danger</span> <span class="token attr-name">v-model</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>option3<span class="token punctuation">"</span></span><span class="token punctuation">></span></span>
        Danger
      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>vs-checkbox</span><span class="token punctuation">></span></span>
      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>vs-checkbox</span> <span class="token attr-name">warn</span> <span class="token attr-name">v-model</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>option4<span class="token punctuation">"</span></span><span class="token punctuation">></span></span>
        warning
      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>vs-checkbox</span><span class="token punctuation">></span></span>
      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>vs-checkbox</span> <span class="token attr-name">dark</span> <span class="token attr-name">v-model</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>option5<span class="token punctuation">"</span></span><span class="token punctuation">></span></span>
        dark
      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>vs-checkbox</span><span class="token punctuation">></span></span>
      <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>vs-checkbox</span> <span class="token attr-name">color</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>#7d33ff<span class="token punctuation">"</span></span> <span class="token attr-name">v-model</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>option6<span class="token punctuation">"</span></span><span class="token punctuation">></span></span>
